package Start_Batch;

public class ConditionOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=100;
		int b=50;
		boolean res=(a>b);
		System.out.println(res);
		System.out.println("===");
		boolean res1=(a<b);
		System.out.println(res1);
		System.out.println("===");
		boolean res2=(a>=b);
		System.out.println(res2);
		System.out.println("===");
		boolean res3=(a<=b);
		System.out.println(res3);
		System.out.println("===");
		boolean res4=(a==b);
		System.out.println(res4);
		System.out.println("===");
		boolean res5=(a!=b);
		System.out.println(res5);
		System.out.println("===");

	}

}
