package Start_Batch;

public class LoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Foor Loop Example:");
		for(int i=1;i<=10;i++)
		{
			System.out.println("Tomorrow will be Backup classes @10am");
		}
		System.out.println("++++++++++++++++++");
		System.out.println("While Loop Example!");
		int i=1;
		while(i<=10)
		{
		 System.out.println("Tomorrow will be Backup Classes @10.30am");
		 i++;
		}
		System.out.println("Deepak Donn!!!!!!!!!!!!!!!!!!!!!!!!!!");

		int j=1;
		do {
			System.out.println("Today Movie Shows Rate is 9rs..");
			j++;
			
		}while(j<=10);
	}

}
